import React ,{useState,useCallback,useEffect} from 'react';
import {BrowserRouter as Router,Route,Redirect,Switch} from 'react-router-dom';

import Users from './user/pages/Users';
import NewPlace from './places/pages/NewPlace';
import UserPlaces from './places/pages/UserPlaces'
import MainNavigation from './shared/components/Navigation/MainNavigation'
import UpdatePlace from './places/pages/UpdatePlace';

import Auth from './user/pages/Auth';

import AuthContext from './shared/context/auth-context'
const App=()=> {

  const [token,setToken]=useState(false);
  const [userId,setUserId]=useState(false);

  const login=useCallback((userId,token,expiration)=>{
    const tokenExpirationDate=expiration||new Date(new Date().getTime()+1000*60*60);
    localStorage.setItem(
      'userData',
      JSON.stringify({userId:userId,token:token,expiration:tokenExpirationDate.toISOString()})
    );
    setUserId(userId);
    setToken(token);
  },[])

  const logout=useCallback(()=>{
    setToken(false);
    setUserId(null);
    localStorage.removeItem('userData');
  },[])

  useEffect(()=>{
    const storedData=JSON.parse(localStorage.getItem('userData'));
    if(storedData && storedData.token){
      login(storedData.userId,storedData.token);
    }
  },[login])

  let routes;

  if(token){
    routes=(
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/places" exact>
          <UserPlaces />
        </Route>
        <Route path="/places/new" exact>
          <NewPlace />
          </Route>
          <Route path="/places/:placeId">
              <UpdatePlace />
          </Route>
        <Redirect to="/" />
      </Switch>
    );
  }else{
    routes=(
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/places" exact>
          <UserPlaces />
        </Route>
        <Route path="/auth">
              <Auth />
            </Route>
          <Redirect to="/auth" />
      </Switch>
    );
  }

  return (
    <AuthContext.Provider
      value={{ isLoggedIn:!!token,token:token , login: login, logout: logout, userId:userId}}
    >
      <Router>
        <MainNavigation />
        <main>
            {routes}
        </main>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
